<script>
// @ is an alias to /src

const langfile = require("@/assets/lang/content.json");
import axios from "axios";
export default {
  name: "ok",
  data: function() {
    return {
      paymentUrl:this.$route.query.paymentUrl,
      leadName: "",
      commentsToProvider: "",
      email: "",
      email2: "",
      coupon: null,
      entrada: this.$route.query.entrada,
      salida: this.$route.query.salida,
      multilanguage: langfile.multilanguage,
      detalles: null,
      language: "es",
      cart: JSON.parse(localStorage.getItem("ccrx_cart")),
      ocupacion: []
    };
  },

  methods: {
    gettimereference: function() {
      var d = new Date();
      var month = "" + (d.getMonth() + 1);
      var day = "" + d.getDate();
      var year = d.getFullYear();
      var hour = d.getHours().toString();
      var minute = d.getMinutes().toString();
      var seconds = d.getSeconds().toString();

      if (month.length < 2) month = "0" + month;
      if (day.length < 2) day = "0" + day;
      if (hour.length < 2) hour = "0" + hour;
      if (minute.length < 2) minute = "0" + minute;
      if (seconds.length < 2) seconds = "0" + seconds;
      return (
        year.toString().substr(2, 2) +
        month.toString() +
        day.toString() +
        hour.toString() +
        minute.toString() +
        seconds.toString()
      );
    },
    book: function() {
      // e.preventDefault();
      // e.stopPropagation()
      var _this = this;
      var d = this.$data;
      var reference = this.gettimereference();

      axios({
        method: "put",
        url:
          "https://demo.quotravel.eu/resources/eyAiY3JlYXRlZCI6ICJGcmkgTWFyIDIyIDEwOjIyOjI5IENFVCAyMDE5IiwgInVzZXJJZCI6ICJhZG1pbiIsICJhZ2VuY3lJZCI6ICIzIn0=/hotel/booking",
        data: {
          language: d.language,
          rateKeys: this.$data.ocupacion,
          bookingReference: reference,
          captchaToken: "",
          leadName: this.$data.leadName,
          commentsToProvider: this.$data.commentsToProvider,
          privateComments: "",
          services: [],
          email: this.$data.email,
          mailingUnwanted: "true"
        }
      })
        .then(function(response) {
          _this.$router.push({
            name: "ok",
            query: {
              response: response,
              cart: _this.$data.cart,

              salida: _this.$data.salida,
              entrada: _this.$data.entrada,
              ocupacion: _this.$route.query.ocupacion
            }
          });
        })
        .catch(function(error) {
          console.log(error);
        });
    },

    formatDatetoview(dtf) {
      if (dtf) {
        let dateArray = String(dtf).split("-");
        let fecha = "";
        switch (this.$data.language) {
          case "es":
            fecha = dateArray[2] + "-" + dateArray[1] + "-" + dateArray[0];
            break;
          case "en":
            fecha = dateArray[1] + "-" + dateArray[2] + "-" + dateArray[0];
            break;
          default:
            fecha = dateArray[2] + "-" + dateArray[1] + "-" + dateArray[0];
            break;
        }
        return fecha;
      }
    },

    formatDate: function(date) {
      var month = date.substr(4, 2);
      var day = date.substr(6, 2);
      var year = date.substr(0, 4);

      var fdat = [day, month, year].join("/");
      return fdat;
    }
  },
  mounted() {}
};
</script>
